import { Component } from 'react';
import './App.css';

import Handlebars from "handlebars";

const html = `<div id="board">
                {{#each cells}}\
                  {{#if link}}\
                    <a href={{link}}><div class="cell {{clazz}}">{{text}}</div></a>\
                  {{else}}\
                    <div class="cell {{clazz}}">{{text}}</div>\
                  {{/if}}\
                {{/each}}\
              </div>\
              <div id="bottombar">\
                <a href="/newgame"><button>New Game</button></a>\
                <button>Undo</button>\
              </div>`;
const template = Handlebars.compile(html);

interface Cell {
  text: String,
  clazz: String,
  link: String
}

class App extends Component {
  cell: Array<Cell> = [{ text: "", clazz: "playable", link: "/play?x=0&y=0"},{ text: "", clazz: "playable", link: "/play?x=1&y=0"},{ text: "", clazz: "playable", link: "/play?x=2&y=0"},
                           { text: "", clazz: "playable", link: "/play?x=0&y=1"},{ text: "", clazz: "playable", link: "/play?x=1&y=1"},{ text: "", clazz: "playable", link: "/play?x=2&y=1"},
                           { text: "", clazz: "playable", link: "/play?x=0&y=2"},{ text: "", clazz: "playable", link: "/play?x=1&y=2"},{ text: "", clazz: "playable", link: "/play?x=2&y=2"}];


  async newGame() {
    const response = await fetch('newgame');
    const j = await response.json();

    for (var i=0; i < j["cells"].length; i++) {
      this.cell[i]["text"] = "x";
      this.cell[i]["clazz"] = j["cells"][i]["clazz"];
      this.cell[i]["link"] = "";
    }
    return this.cell;
  };

  render() {
    console.log(window.location.href);
    if (window.location.href == "http://localhost:3000/newgame") {
      console.log("HI");
      this.cell[0]["text"] = "o";
      const tmp = fetch('newgame');
      console.log(tmp);
    }
    return (
      <div className="App">
        <div dangerouslySetInnerHTML={{ __html: template({cells: this.cell}) }} />
      </div>
    );
  }
}

export default App;
