import { Component } from 'react';
import './App.css';

import Handlebars from "handlebars";

const html = `<div id="board">
                {{#each cells}}\
                  {{#if link}}\
                    <a href={{link}}><div class="cell {{clazz}}">{{text}}</div></a>\
                  {{else}}\
                    <div class="cell {{clazz}}">{{text}}</div>\
                  {{/if}}\
                {{/each}}\
              </div>\
              <div id="bottombar">\
                <a href="/newgame"><button>New Game</button></a>\
                <button>Undo</button>\
              </div>`;
const template = Handlebars.compile(html);



interface Cell {
  text: String,
  clazz: String,
  link: String
};

interface Gameplay {
  cells: Array<Cell>
}

var cell: Array<Cell> = [{ text: "", clazz: "playable", link: "/play?x=0&y=0"},{ text: "", clazz: "playable", link: "/play?x=1&y=0"},{ text: "", clazz: "playable", link: "/play?x=2&y=0"},
                         { text: "", clazz: "playable", link: "/play?x=0&y=1"},{ text: "", clazz: "playable", link: "/play?x=1&y=1"},{ text: "", clazz: "playable", link: "/play?x=2&y=1"},
                         { text: "", clazz: "playable", link: "/play?x=0&y=2"},{ text: "", clazz: "playable", link: "/play?x=1&y=2"},{ text: "", clazz: "playable", link: "/play?x=2&y=2"}];

var data: Gameplay = {
  cells: cell
};

class App extends Component<{}, Gameplay> {
  constructor({}) {
    super({});
    this.state = {
      cells: cell
    }
  }

  async newGame() {
    const response = await fetch('newgame');
    const j = await response.json();

    for (var i=0; i < j["cells"].length; i++) {
      this.state.cells[i]["text"] = "x";
      this.state.cells[i]["clazz"] = j["cells"][i]["clazz"];
      this.state.cells[i]["link"] = j["cells"][i]["link"];
    }
  }

  async componentDidMount() {
    var href = window.location.href
    if (href === "http://localhost:3000/newgame") {
      this.newGame();
    }
  }


  render() {
    return (
      <div className="App">
        <div dangerouslySetInnerHTML={{ __html: template({cells: this.state.cells}) }} />
      </div>
    );
  }
}

export default App;
